const additemstocart = (cartitems, itemtobeadded) => {
  const existingcartitem = cartitems.find((cartitems.id = itemtobeadded.id));

  if (existingcartitem) {
    return cartitems.map((cartitem) =>
      cartitem.id === itemtobeadded.id
        ? { ...cartitem, quantity: quantity + 1 }
        : cartitem
    );
  }

  return [...cartitems, { ...itemtobeadded, quantity: 1 }];
};
