const INITIAL_STATE = {
  hidden: false,
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SHOW_CARTITEMS_MODAL":
      return {
        ...state,
        currentUser: !state.hidden,
      };
    default:
      return state;
  }
};

export default cartReducer;
