import {
  takeEvery,
  call,
  put,
  take,
  takeLatest,
  delay,
  all,
} from "redux-saga/effects";

import {
  auth,
  provider,
  createUserProfileDocument,
} from "../../FireBase/firebase.config";

import { googleSignInCompleted, googleSignInError } from "./user-actions";

export function* GoogleSignInStart() {
  yield takeLatest("GOOGLE_SIGNIN_START", fetchUserFromFirebaseAsync);
}

function* fetchUserFromFirebaseAsync() {
  try {
    const { user } = yield auth.signInWithPopup(provider);

    const userref = yield call(createUserProfileDocument, user);
    //console.log(userref);
    const snapshot = yield userref.get();
    console.log({
      ...snapshot.data(),
    });
    yield call(
      googleSignInCompleted({
        id: snapshot.id,
        ...snapshot.data(),
      })
    );
    console.log(snapshot.id);
  } catch (error) {
    yield put(googleSignInError(error));
  }
}

export function* userRootSaga() {
  yield all([call(GoogleSignInStart)]);
}
