const INITIAL_STATE = {
  hidden: false,
  cartItems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SHOW_CARTITEMS_MODAL":
      return {
        ...state,
        hidden: !state.hidden,
      };
    case "ADD_ITEMS":
      return {
        ...state,
        cartItems: [...state.cartItems, action.payload],
      };
    default:
      return state;
  }
};

export default cartReducer;
