import React from "react";
import CollectionOverview from "../../components/CollectionsOverview/CollectionOverview";
import { Route } from "react-router-dom";
import CollectionPage from "../CollectionPage/CollectionPage";
import {
  firestore,
  convertCollectionsArrayToObject,
} from "../../FireBase/firebase.config";
import { connect } from "react-redux";
import { updateShopItems } from "../../redux/shop/shop-actions";
import WithSpinner from "../../components/WithSpinner/WithSpinner";
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);

class ShopPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isloading: true,
    };
  }
  unsubscribe = null;
  componentDidMount() {
    const { updateCollectionsMap } = this.props;
    const collectionRef = firestore.collection("collections");
    this.unsubscribe = collectionRef.onSnapshot(async (snapshot) => {
      const collectionsMap = convertCollectionsArrayToObject(snapshot);

      await updateCollectionsMap(collectionsMap);
      this.setState({ isloading: false });
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  render() {
    const { match } = this.props;
    const { isloading } = this.state;
    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverviewWithSpinner isloading={isloading} {...props} />
          )}
        />
        <Route
          exact
          path={`${match.path}/:categoryid`}
          render={(props) => (
            <CollectionPageWithSpinner isloading={isloading} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  updateCollectionsMap: (collectionsMap) =>
    dispatch(updateShopItems(collectionsMap)),
});

export default connect(null, mapDispatchToProps)(ShopPage);
