import {
  takeEvery,
  call,
  put,
  take,
  takeLatest,
  delay,
  all,
} from "redux-saga/effects";

import {
  auth,
  provider,
  createUserProfileDocument,
} from "../../FireBase/firebase.config";

import { googleSignInCompleted, googleSignInError } from "./user-actions";

function* GoogleSignInStart() {
  yield takeLatest("GOOGLE_SIGNIN_START", googleSignInFirebaseAsync);
}
function* emailSignInFirebaseAsync({ payload: { email, password } }) {
  console.log(email, password);
  try {
    const userref = yield auth.signInWithEmailAndPassword(email, password);
    console.log(userref);
  } catch (error) {
    console.log(error.message);
  }
}

export function* EmailSignInstart() {
  yield takeLatest("EMAIL_SIGNIN_START", emailSignInFirebaseAsync);
}

function* googleSignInFirebaseAsync() {
  try {
    const { user } = yield auth.signInWithPopup(provider);

    const userref = yield call(createUserProfileDocument, user);
    //console.log(userref);
    const snapshot = yield userref.get();
    console.log({
      ...snapshot.data(),
    });
    yield put(
      googleSignInCompleted({
        id: snapshot.id,
        ...snapshot.data(),
      })
    );
    console.log(snapshot.id);
  } catch (error) {
    yield put(googleSignInError(error));
  }
}

export function* userRootSaga() {
  yield all([call(GoogleSignInStart)]);
}
