import React from "react";
import CollectionOverview from "../../components/CollectionsOverview/CollectionOverview";
import { Route } from "react-router-dom";
import CollectionPage from "../CollectionPage/CollectionPage";

import { connect } from "react-redux";
import { fetchShopItemFromFirebaseAsync } from "../../redux/shop/shop-actions";
import WithSpinner from "../../components/WithSpinner/WithSpinner";
import { selectIsFetching } from "../../redux/shop/shop-selectors";
import { createStructuredSelector } from "reselect";
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);

class ShopPage extends React.Component {
  componentDidMount() {
    const { fetchShopItemFromFirebaseAsync } = this.props;
    fetchShopItemFromFirebaseAsync();
  }

  render() {
    const { match, isFetching } = this.props;

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverviewWithSpinner isloading={isFetching} {...props} />
          )}
        />
        <Route
          exact
          path={`${match.path}/:categoryid`}
          render={(props) => (
            <CollectionPageWithSpinner isloading={isFetching} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchShopItemFromFirebaseAsync: () =>
    dispatch(fetchShopItemFromFirebaseAsync()),
});

const mapStateToProps = createStructuredSelector({
  isFetching: selectIsFetching,
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
